DROP TABLE IF EXISTS `userforum`;
CREATE TABLE `userforum` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL,
  `fid` int(11) NOT NULL,
  `uForumGrade` int(11) NOT NULL DEFAULT '1',
  `uForumExperience` int(11) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `userforum_uidfk` (`uid`),
  KEY `userforum_fidfk` (`fid`),
  CONSTRAINT `userforum_fidfk` FOREIGN KEY (`fid`) REFERENCES `forum` (`id`),
  CONSTRAINT `userforum_uidfk` FOREIGN KEY (`uid`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

