-- ----------------------------
-- Table structure for `usergame`
-- ----------------------------
DROP TABLE IF EXISTS `usergame`;
CREATE TABLE `usergame` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL,
  `gid` int(11) NOT NULL,
  `uCharaGrade` int(11) NOT NULL DEFAULT '1',
  `uCharaUid` int(11) DEFAULT NULL,
  `uCharaName` varchar(64) DEFAULT '杀手',
  `isDefaultChara` int(11) DEFAULT '0' COMMENT '1表示默认角色，0表示非默认角色。',
  `uChannel` varchar(11) DEFAULT NULL,
  `verCode` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `usergame_uidfk` (`uid`),
  KEY `usergame_gidfk` (`gid`),
  CONSTRAINT `usergame_gidfk` FOREIGN KEY (`gid`) REFERENCES `game` (`id`),
  CONSTRAINT `usergame_uidfk` FOREIGN KEY (`uid`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

