  PRIMARY KEY (`id`),
  KEY `userShipAddress_uidfk` (`uid`),
  CONSTRAINT `userShipAddress_uidfk` FOREIGN KEY (`uid`) REFERENCES `user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

